(window.webpackJsonp=window.webpackJsonp||[]).push([[34],{828:function(v,_,t){"use strict";t.r(_);var a=t(45),r=Object(a.a)({},(function(){var v=this,_=v.$createElement,t=v._self._c||_;return t("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[t("h1",{attrs:{id:"做自己-给人自由幻觉的陷阱"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#做自己-给人自由幻觉的陷阱"}},[v._v("#")]),v._v(" “做自己”：给人自由幻觉的陷阱")]),v._v(" "),t("p",[v._v("社交网络、大众媒体渲染、朋友圈中，大家最抵抗无能的就是一个词：“做自己”。但是为什么越是强调“做自己”，最后大家的做法都差不多？")]),v._v(" "),t("p",[v._v("雷同的ins风，雷同的网红美女，雷同的彩妆，雷同的bm装、健身室打卡和“高街女帝”。这阵子流行某只口红，你会发现最后人手一只。")]),v._v(" "),t("p",[v._v("这是因为，在社交网络和大众媒体渲染下，“做自己”的本质是把自己变成商品：")]),v._v(" "),t("p",[v._v("每个人都在打造自己的“人设”、生产自己，“自己”成了一个商品，以供他人消费。本质就是将自己作为品牌、“打造自己”。")]),v._v(" "),t("p",[v._v("在这个过程中，每个人都将过多的注意力、精力投放在“打造自己”上，陷入疯狂的、病态的自恋。")]),v._v(" "),t("h2",{attrs:{id:"自恋vs自爱"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#自恋vs自爱"}},[v._v("#")]),v._v(" 自恋vs自爱")]),v._v(" "),t("p",[v._v("自恋 不等于 自爱。前者病态，后者健康。")]),v._v(" "),t("blockquote",[t("p",[v._v("自恋：人过分将注意力、时间、精力、资源投放在自己身上，过度沉迷“自己”的形象上。自恋者将自己与他人（或者说是理想的自我形象）不断地比较，从而产生各种自我嫌弃、焦虑与自我膨胀、自大。极度的自卑和自我嫌恶，与极度的自大和自我膨胀，都是自恋者的一体两面。")])]),v._v(" "),t("p",[v._v("比如说，沉迷整容的人会对自己的每个器官都不满意，恨不得把每个器官都削成跟范冰冰/迪丽热巴等（理想的自我形象）一样的标准美丽五官，于是塑造了一批批量产式的整容脸网红美女。")]),v._v(" "),t("blockquote",[t("p",[v._v("自爱：人而是在与世界、与社会、与不同的人事物学习对话中，理解了多样性的同时，接纳了自己的多样性。通过欣赏世界的多样性，我们学会欣赏自己，“怎么样都挺好的”。因此自爱的人不会有自恋者的焦虑和自我嫌恶。")])]),v._v(" "),t("h2",{attrs:{id:"做自己-的运行机制"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#做自己-的运行机制"}},[v._v("#")]),v._v(" “做自己”的运行机制")]),v._v(" "),t("p",[v._v("现代社交网络鼓吹的“做自己”，是前者而不是后者，本质在鼓励你过度沉迷地自我形象，忽视并隔离自己与世界、与他人的关系。我们在社交网络中，在各种大众小众的以 穿着打扮风格为核心的圈子（美妆圈、汉服圈、lo圈、jk圈、潮人圈）里，看到了“理想完美的自我形象”。")]),v._v(" "),t("p",[v._v("社交网络使我们轻松接触到各种圈子，它们如同百货商店的陈列架一样，将所有的“自我形象”陈列展出，让我们挑选“理想的自我”。")]),v._v(" "),t("p",[v._v("看起来虽然“多样”，但是实际上每种“理想自我”的顶端、追捧的形象，都只有一种样子。作为挑选者，我们看起来“自由”，但其实只是“商家让我们挑选”的自由，一种“自由的幻觉”，表象的“自由”让你“没必要”去想象和探索更多的可能性。")]),v._v(" "),t("p",[v._v("模板是有限的，不符合模板的形象将会被无情地驱逐和隔离。")]),v._v(" "),t("p",[v._v("通过挑选，我们选中了某种“自我”，并不断地学习、深入了解这种“自我形象”。然后以此为模板，每个人都将注意力投放“打造自我”上，打造的目标就是无限接近自己选中的“圈子内认定的最高理想形象”。")]),v._v(" "),t("p",[v._v("如果发现自己有一丝一毫跟“理想自我”有出入，就会产生无止境的焦虑和嫌恶。")]),v._v(" "),t("p",[v._v("而如果我们恰好已经贴近了这种“理想自我”，我们就会受到圈子的追捧。圈子里所有人都有着相同的“自我形象”目标，进圈子越久，越发现大家都是复制品。其他人通过“点赞”不费力气地肯定了贴近理想形象的我，使得我的自恋不断加深、不断膨胀，企图得到圈子更多的点赞与认同。")]),v._v(" "),t("p",[v._v("这种自我膨胀是空虚的，它使得我们日渐封闭，日渐隔离，困囿于小圈子。而圈子内，你会发现全是跟你相似甚至相同的人，我们会发现自己很难接触到新的东西，只是日复一日地沉浸在已有的东西里日渐空虚无聊，酝酿抑郁和自我嫌恶，并与世界隔离，在圈子的“回音室”中无限重复同样的声音，自我异化。")]),v._v(" "),t("p",[v._v("而资本通过向你贩卖“理想自我”的形象，并告诉你，你和这种“理想自我”的差距是可以通过消费来克服的。")]),v._v(" "),t("p",[v._v("通过宣传令人着魔的“做自己”，资本成功说服大家过度将精力投放在“打造自我”上，并塑造出一个个流水线的“人造美人”，令人成为“做自己”的奴隶，乖乖贡献时间和金钱。")]),v._v(" "),t("h2",{attrs:{id:"自我价值-靠的是建立与世界的关系"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#自我价值-靠的是建立与世界的关系"}},[v._v("#")]),v._v(" “自我价值”靠的是建立与世界的关系")]),v._v(" "),t("p",[v._v("我认为，一个人，要认识自我、认识并建立自己的价值，靠的绝对不是自我沉迷的自恋。")]),v._v(" "),t("p",[v._v("哲学上对于人如何确立自我价值是这样论述的：人只能通过他者才能认识到自己。通过触摸其他的物件，我们感受到他者，也感受到了“我与他者的边界”，即自我的真实存在感。")]),v._v(" "),t("p",[v._v("自我价值，必然是建立在对其他人、对世界的恰当认识（而非误解）上的。")]),v._v(" "),t("p",[v._v("比如说，你长了一双招风耳，小时候小伙伴都因为招风耳嘲笑你，看着其他小伙伴的标准耳朵，你觉得招风耳是你必须“纠正”和割舍的一部分，你一天天看着自己和他人的区别，一天天自我讨厌，希望长大后做整形手术纠正。")]),v._v(" "),t("p",[v._v("但是长大了，你发现有着招风耳的美女原来可以很漂亮，“招风耳”成了“精灵耳”，原来招风耳也可以是美的。于是你消除了自我嫌恶，重新发现自己招风耳的美。")]),v._v(" "),t("p",[v._v("在上述的故事中，“我”在一开始被设立的规范下，将招风耳视为“理想自我”的他者，不属于理想自我的一部分，所以必须切割。而这种自我厌恶必须要等到我发现其他招风耳的人的美，认识和欣赏到他人的美以后，我才能认识到自己的价值“招风耳是美的”。")]),v._v(" "),t("p",[v._v("我的自我价值确立，依赖于我对与我不一样的人、对世界的学习、认识、欣赏、接纳和包容。")]),v._v(" "),t("p",[v._v("人通过获取深刻的知识、深刻的体验，确立自我价值。深刻的知识和体验都源自于对与自己不同的他人和世界的了解、认识才能获得。")]),v._v(" "),t("p",[v._v("只有在专业上的学习、自我发展，能让人真正确立自我价值。")]),v._v(" "),t("p",[v._v("而雷同的“做自己”的“自由圈子”文化并没有办法实现这点。它只会让你越来越空洞，越来越空虚。")]),v._v(" "),t("p",[t("a",{attrs:{href:"https://www.douban.com/note/806219758/",target:"_blank",rel:"noopener noreferrer"}},[v._v("https://www.douban.com/note/806219758/"),t("OutboundLink")],1)])])}),[],!1,null,null,null);_.default=r.exports}}]);