(window.webpackJsonp=window.webpackJsonp||[]).push([[39],{841:function(t,e,n){"use strict";n.r(e);var a=n(45),s=Object(a.a)({},(function(){var t=this,e=t.$createElement,n=t._self._c||e;return n("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[n("h1",{attrs:{id:"打拳多年-真的遇到太多这种人了"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#打拳多年-真的遇到太多这种人了"}},[t._v("#")]),t._v(" 打拳多年，真的遇到太多这种人了")]),t._v(" "),n("p",[t._v("打拳多年，真的遇到太多这种人了。喜欢忧天悯人地说任何被女权博主指出来是性别问题的问题不是性别问题，是阶级问题甚至就是普世问题，“可能发生在每个人身上”。他们不止在女权博主的页面上这样做，还经常冲进受害女性控诉的页面这样做。")]),t._v(" "),n("p",[t._v("很“有意思”地是，他们一方面表达自己悲天悯人，觉得每个人都可能是受害者，包括男性；另一方面对于既成的女性受害者的遭遇，他们是无视的。他们关心未来可能受害的男性，远大于现在已经受害的女性。然后他们把这个称之为理性和善良。")]),t._v(" "),n("p",[t._v("他们忽视概率，忽视比例，忽视女性所遭遇的痛苦几千倍于男性，也忽视女性受害者的人数远高于男性。前几个月，我天才地把这个行为称之为“加杠杆”。加杠杆之后，便“平等”了，一个男性受害者加上杠杆抵得过千百个女性受害者，同理，一个女性犯罪者/坏人也抵得上千百个男性犯罪者/坏人。这个杠杆的名字就叫“男尊女卑”。")]),t._v(" "),n("p",[t._v("他们才是最希望男性也成为受害者的人，不然他们的悲天悯人和理性善良的形象何以立足呢？有杠杆也得有原始数据支撑才行啊，毕竟再大的杠杆乘以零还是零。")]),t._v(" "),n("p",[t._v("对了，更有意思的是，对于那种旅居海外的人，一旦这种事发生在国籍/种族上，他们成为了加杠杆原理中“卑”的那一方，就开始意识到问题的本质了：这就是歧视！这是种族歧视！这是对我身为中国人和亚裔的歧视！")]),t._v(" "),n("p",[t._v("但他们会因此对女性有同理心吗？绝大部分人都是没有的。他们只会更恨自己的女性同胞，觉得自己被歧视了（其实更大的概率是享受不到中国社会中的男权福利了）、女性却能因为政治正确的原因踩到他们头上，气死了。")]),t._v(" "),n("p",[t._v("气死你算了。")]),t._v(" "),n("p",[t._v("Sciencecat的广播 "),n("a",{attrs:{href:"https://www.douban.com/people/sciencecat/status/3573235621/",target:"_blank",rel:"noopener noreferrer"}},[t._v("https://www.douban.com/people/sciencecat/status/3573235621/"),n("OutboundLink")],1)])])}),[],!1,null,null,null);e.default=s.exports}}]);